# Declare for spin for processing - START

# Can be changed for something else if you don't like a spinning cursor

declare -a spinner
Spinner=(/ - \\ \| / - \\ \| )
Spinnerpos=0

function update_spinner() {
        printf "\b"${Spinner[$Spinnerpos]}
        (( Spinnerpos=(Spinnerpos +1) %8))
}

